import { useState, useEffect } from 'react';

const Timer = () => {
  const [elapsedTime, setElapsedTime] = useState('');

  useEffect(() => {
    // Set the target date to 1 AM on October 2nd
    const targetDate = new Date('2024-10-02T01:00:00');

    const updateTimer = () => {
      const currentDate = new Date();
      const difference = currentDate - targetDate; // Time difference in milliseconds

      if (difference >= 0) {
        // Convert milliseconds into HH:MM:SS format
        const hours = Math.floor(difference / (1000 * 60 * 60));
        const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((difference % (1000 * 60)) / 1000);

        const timeString = `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;

        setElapsedTime(timeString);  // Update the state with formatted string
      } else {
        setElapsedTime('00:00:00'); // If current time is before 1 AM on October 2nd
      }
    };

    // Initial call to set the timer
    updateTimer();

    // Update every second
    const interval = setInterval(updateTimer, 1000);

    // Cleanup interval on component unmount
    return () => clearInterval(interval);
  }, []);

  return elapsedTime; // Return the elapsed time string
};

export default Timer;
