import React, { useEffect, useState } from 'react';
import { Menu, MenuButton, MenuItem, MenuItems } from '@headlessui/react';
import { ChevronDownIcon } from '@heroicons/react/20/solid';
import { ArrowDownCircleIcon } from '@heroicons/react/20/solid';

const url = 'https://docs.google.com/spreadsheets/d/e/2PACX-1vTSh1oblyT232IiyQk4k6cDQyjIPWWHGcMWB2DtCRB6VAnfncNjaAnEjdbMW3CLKLtAbKwBBZU-yn0n/pub?gid=920888741&single=true&output=csv';

const DataDisplay = () => {
    const [data, setData] = useState([]);
    const [selectedTeam, setSelectedTeam] = useState('All_Teams');
    const [teams, setTeams] = useState([]);

    const parseCSV = (textData) => {
        const rows = textData.split('\n').slice(1); // Skip the header row
        const parsedData = rows.map(row => {
            const [dateTime, teamName, teamId, amountBefore, amountAfter, description, imageLink] = row.split(',');
            return {
                dateTime,
                teamName,
                teamId,
                amountBefore,
                amountAfter,
                imageLink,
                description,
            };
        });
        setData(parsedData);
        // Extract unique team names
        const uniqueTeams = Array.from(new Set(parsedData.map(item => item.teamName)));
        setTeams(uniqueTeams);
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const textData = await response.text();
                parseCSV(textData);
            } catch (error) {
                console.error('Error fetching the data:', error);
            }
        };

        fetchData();
        const interval = setInterval(fetchData, 5000); // Update every 5 seconds
        return () => clearInterval(interval); // Cleanup interval on unmount
    }, []); // No need to include fetchData here anymore

    // Filtered data based on the selected team
    const filteredData = selectedTeam === 'All_Teams' ? data : data.filter(item => item.teamName === selectedTeam);

    return (
        <div className="data-display py-8 flex flex-col gap-4 justify-center items-center w-full">
            <div className='flex flex-row'>
                <h1 className='text-red-950 font-serif text-lg'>Live ScoreBoard</h1>
                <span className="relative flex h-3 w-3">
                    <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                    <span className="relative inline-flex rounded-full h-3 w-3 bg-red-500"></span>
                </span>
            </div>

            {/* Dropdown for team selection */}
            <Menu as="div" className="relative inline-block text-left mb-4">
                <div>
                    <MenuButton className="flex flex-row justify-center gap-x-1.5 rounded-lg bg-sky-200 w-60 px-3 py-2 text-sm font-semibold text-gray-800 shadow-sm ring-2 ring-inset ring-sky-400 hover:bg-sky-300">
                        {selectedTeam} <ChevronDownIcon className="-mr-1 h-5 w-5 text-sky-400" />
                    </MenuButton>
                </div>
                <MenuItems className="absolute right-0 z-10 mt-2 w-60 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none">
                    <div className="py-1">
                        <MenuItem>
                            <button
                                onClick={() => setSelectedTeam('All')}
                                className=" block w-full px-4 py-2 text-left text-sm text-gray-700 hover:bg-orange-200"
                            >
                                All
                            </button>
                        </MenuItem>
                        {teams.map((team, index) => (
                            <MenuItem key={index}>
                                <button
                                    onClick={() => setSelectedTeam(team)}
                                    className="block w-full px-4 py-2 text-left text-sm text-gray-700 hover:bg-orange-100"
                                >
                                    {team}
                                </button>
                            </MenuItem>
                        ))}
                    </div>
                </MenuItems>
            </Menu>

            {filteredData.length > 0 ? (
                <table className="table-auto w-full border-collapse border border-gray-200 bg-slate-100 text-left rounded-lg text-xs md:text-sm">
                    <thead>
                        <tr>
                            <th className='p-4'>Date & Time</th>
                            <th className='p-4'>Team Name</th>
                            {/* <th className='p-4'>Team ID</th> */}
                            <th className='p-4'>Amount Before</th>
                            <th className='p-4'>Amount After</th>
                            <th className='p-4'>Description</th>
                            <th className='p-4'>Image</th>

                        </tr>
                    </thead>
                    <tbody>
                        {filteredData.map((item, index) => (
                            <tr key={index} className='hover:bg-orange-200'>
                                <td className='px-4'>{item.dateTime}</td>
                                <td className='px-4'>{item.teamName}</td>
                                {/* <td className='px-4'>{item.teamId}</td> */}
                                <td className='px-4'>{item.amountBefore}</td>
                                <td className='px-4'>{item.amountAfter}</td>
                                <td className='px-4'>{item.description}</td>
                                <td className='px-4 place-content-center'>
                                    <a href={item.imageLink} target="_blank" rel="noopener noreferrer" className='inline-flex'>
                                        <ArrowDownCircleIcon className="ml-2 h-5 w-5 text-sky-400" aria-hidden="true" />
                                        <img src={item.imageLink} alt="link" className="w-16 h-16 object-cover" />

                                    </a>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No transactions available for this team.</p>
            )}
        </div>
    );
};

export default DataDisplay;
